import React from 'react'
import "./NavBar.css"
import { Link } from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles';
import ls from "local-storage"
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
    <div
        role="tabpanel"
        hidden={value !== index}
        id={`nav-tabpanel-${index}`}
        aria-labelledby={`nav-tab-${index}`}
        {...other}
    >
        {value === index && (
        <Box p={3}>
            <Typography>{children}</Typography>
        </Box>
        )}
    </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `nav-tab-${index}`,
        'aria-controls': `nav-tabpanel-${index}`,
    };
}

function LinkTab(props) {
return (
    <Tab
        component="a"
        onClick={(event) => {
        event.preventDefault();
        }}
        {...props}
    />
);
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.paper,
    },
}));

const NavBar = (props) => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    const localClickHandler = (e) => {
        ls.set("currentUser", null)
        // props.logoutHandler()
    }

    return (
        props.currentUser === null ?
        <></>
        :
        <div className="header">

            {/* <img src="https://fontmeme.com/permalink/210115/72a4a804c01a2fa22cb39362f6132c1d.png"/> */}

            <Paper className={classes.root}>
                <Tabs
                    value={value}
                    onChange={handleChange}
                    indicatorColor="primary"
                    textColor="primary"
                    centered
                >
                    <Link to="/profile">
                        <Tab label="Profile"/>
                    </Link>
                    <Link to="/pickpage">
                        <Tab label="PickPage"/>
                    </Link>

                </Tabs>
            </Paper>


            <AppBar position="static">
                <Tabs
                variant="fullWidth"
                value={value}
                onChange={handleChange}
                aria-label="nav tabs example"
                >
                <LinkTab label="Profile" href="/profile" {...a11yProps(0)} />
                <LinkTab label="Pick Page" href="/pickpage" {...a11yProps(1)} />
                <Link to="/welcome">
                        <Tab label="Log Out" />
                </Link>
                <LinkTab label="Log Out" href="/welcome" {...a11yProps(2)} />
                </Tabs>
            </AppBar>
        </div>
    )

}

export default NavBar
