import {React, useState} from 'react'
import "./NavBar.css"
import ls from "local-storage"
import { Link } from 'react-router-dom'
import { push as Menu } from 'react-burger-menu';


class NavBar extends React.Component {
    state = {
        menuOpen: false
    }

    handleStateChange (state) {
        this.setState({menuOpen: state.isOpen})  
    }

    closeMenu() {
        this.setState({menuOpen: false})
    }

    toggleMenu () {
        this.setState(state => ({menuOpen: !state.menuOpen}))
    }
    render() {
        return (
            props.currentUser === null ?
                <></>
                :
                <div className="navnav">

                    <img
                        className="navlogo"
                        src="https://fontmeme.com/permalink/210115/72a4a804c01a2fa22cb39362f6132c1d.png"
                        alt="PickFlix"
                    />
                    <Menu
                        right
                        isOpen={this.state.setMenuOpen}
                        onStateChange={(menuOpen) => this.handleStateChange(menuOpen)}
                        pageWrapId={"page-wrap"}
                        outerContainerId={"outer-container"}>
                        <Link to="/home" className="menuitem">
                            <button className="navbutton" > Home </button>
                        </Link>
                        <Link to="/pickpage" className="menuitem">
                            <button className="navbutton" > Pick Page </button>
                        </Link>
                        <Link to="/profile" className="menuitem">
                            <button className="navbutton" > Profile </button>
                        </Link>
                        <Link to="/welcome" className="menuitem">
                            <button className="navbutton" onClick={localClickHandler}> Log Out </button>
                        </Link>
                    </Menu>
                </div>
        )
    }

}

export default NavBar
